                rightDrill.style.top = '100px';

                // Re-enable button after animation completes
                setTimeout(() => {
                    rightDrilling = false;
                    document.getElementById('special_btn').disabled = false;
                }, 1000);
            }, 500);
        }, 1000);
    }, 1000);
